package com.ln.controller;import com.ln.entity.User;import com.ln.repository.UserRepository;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;/** * @author ：leining * @description：TODO */@RestControllerpublic class UserController {    private final Logger LOGGER = LoggerFactory.getLogger(UserController.class);    @Autowired    private UserRepository userRepository;    @GetMapping("/{id}")    public User findById(@PathVariable Long id) {        User user = this.userRepository.findById(id).orElse(null);        return user;    }}